// src/pages/SearchResults.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from '../api/axios';
import MedicineCard from '../components/common/MedicineCard';

const SearchResults = () => {
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const searchQuery = new URLSearchParams(location.search).get('q');

  // useEffect(() => {
  //   const fetchResults = async () => {
  //     try {
  //       setLoading(true);
  //       const response = await axios.get(`/medicines?name=${searchQuery}`);
  //       setResults(response.data);
  //       setError(null);
  //     } catch (err) {
  //       setError('Failed to fetch results. Please try again.');
  //     } finally {
  //       setLoading(false);
  //     }
  //   };

  //   if (searchQuery) {
  //     fetchResults();
  //   }
  // }, [searchQuery]);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/medicines?name=${searchQuery}`);
        console.log('Fetched results:', response.data); // Add this line to debug
        setResults(response.data);
        setError(null);
      } catch (err) {
        setError('Failed to fetch results. Please try again.');
      } finally {
        setLoading(false);
      }
    };
  
    if (searchQuery) {
      fetchResults();
    }
  }, [searchQuery]);
  

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-2xl font-bold mb-6">
        Search Results for "{searchQuery}"
      </h2>
      {results.length === 0 ? (
        <p className="text-center text-gray-600">
          No medicines found matching your search.
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {results.map((medicine, index) => (
            // Fallback to index if medicine._id is missing
            <MedicineCard key={medicine._id || index} medicine={medicine} />
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchResults;
