import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from '../api/axios';
import MedicineCard from '../components/common/MedicineCard';

const SearchResults = () => {
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const searchQuery = new URLSearchParams(location.search).get('q');

  useEffect(() => {
    const fetchResults = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/medicines?name=${searchQuery}`);
        console.log("Fetched medicines:", response.data); // Log the response
        setResults(response.data);
        setError(null);
      } catch (err) {
        console.error("Error fetching results:", err);
        setError('Failed to fetch results. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    if (searchQuery) fetchResults();
  }, [searchQuery]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Search Results for "{searchQuery}"</h2>
      {results.length > 0 ? (
        // <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        //   {results.map((medicine) => (
        //     <MedicineCard key={medicine._id} medicine={medicine} />
        //   ))}
        // </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {results.map((medicine, index) => (
            <MedicineCard key={medicine._id || index} medicine={medicine} />
          ))}
        </div>

      ) : (
        <p>No medicines found for your query.</p>
      )}
    </div>
  );
};

export default SearchResults;
