// src/pages/MedicineDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from '../api/axios';
import { useAuth } from '../context/AuthContext';

const MedicineDetails = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [medicine, setMedicine] = useState(null);
  const [pharmacies, setPharmacies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [medicineRes, pharmaciesRes] = await Promise.all([
          axios.get(`/medicines/${id}`),
          axios.get(`/pharmacies?medicineId=${id}`)
        ]);
        setMedicine(medicineRes.data);
        setPharmacies(pharmaciesRes.data);
        setError(null);
      } catch (err) {
        setError('Failed to fetch medicine details. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const toggleFavorite = async () => {
    try {
      if (isFavorite) {
        await axios.delete(`/favorites/${id}`);
      } else {
        await axios.post('/favorites', { medicineId: id });
      }
      setIsFavorite(!isFavorite);
    } catch (err) {
      console.error('Failed to update favorites:', err);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error || !medicine) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-lg overflow-hidden">
        <div className="p-6">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                {medicine.name}
              </h1>
              <p className="text-gray-600 mb-4">
                Manufacturer: {medicine.manufacturer}
              </p>
            </div>
            {user && (
              <button
                onClick={toggleFavorite}
                className={`px-4 py-2 rounded-md text-sm font-medium ${
                  isFavorite
                    ? 'bg-red-600 text-white'
                    : 'bg-gray-200 text-gray-700'
                }`}
              >
                {isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
              </button>
            )}
          </div>
          <div className="mt-6">
            <h2 className="text-xl font-semibold mb-4">Available at Pharmacies</h2>
            <div className="space-y-4">
              {pharmacies.map((pharmacy) => (
                <div
                  key={pharmacy._id}
                  className="border rounded-lg p-4 hover:shadow-md transition-shadow"
                >
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="font-semibold text-lg">
                        {pharmacy.name}
                      </h3>
                      <p className="text-gray-600">{pharmacy.address}</p>
                    </div>
                    <div className="text-right">
                      <p className="text-2xl font-bold text-green-600">
                        ${pharmacy.medicines.find(m => m.medicineId === id)?.price.toFixed(2)}
                      </p>
                      <p className="text-sm text-gray-500">
                        {pharmacy.medicines.find(m => m.medicineId === id)?.inStock
                          ? 'In Stock'
                          : 'Out of Stock'}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MedicineDetails;
